import UserApiService from "../../../Service/UserApiService";
import { useState } from "react";
import { Modal } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import React from "react";

const ForgotPassword = ({
  showForgotPassword,
  setShowForgotPassword,
  setDisplay,
}) => {
  const [title, setTitle] = useState(<span>Forgot Password</span>);
  const [emailForgotPassword, setEmailForgotPassword] = useState("");
  const [emailLabel, setEmailLabel] = useState(
    <label htmlFor="emailForgotPassword" className="col-form-label">
      Enter email<span style={{ color: "red" }}>*</span>
    </label>
  );

  const getLoading =  ()=>{
    if(localStorage.getItem("localLoading")){
      console.log("Loading...");
      return "Loading...";
    }else{
      console.log("Not L...");
      return "Get Password"
    }
  }

  const handleClose = () => {
    setTitle(<span>Forgot Password</span>);
    setEmailForgotPassword("");
    setEmailLabel(
      <label htmlFor="emailForgotPassword" className="col-form-label">
        Enter email<span style={{ color: "red" }}>*</span>
      </label>
    );
    localStorage.removeItem("localLoading");
    setShowForgotPassword(false);
    setDisplay(true);
  };

  const getCredentials = () => {
    // emailForgotPassword is empty
    if (emailForgotPassword.length === 0) {
      setEmailLabel(
        <label htmlFor="emailForgotPassword" className="col-form-label">
          <span style={{ color: "red" }}>
            Enter Email<span style={{ color: "red" }}>**</span>
          </span>
        </label>
      );


    } else {
      // API CALL
      UserApiService.forgotPassword(emailForgotPassword)
        .then((response) => {
          setTimeout(() => {
            localStorage.removeItem("localLoading");
            const data = response.data;
            if (data === "notFound") {
              setTitle(
                <span style={{ color: "red" }}>
                  Account does not exist, Create new Account
                </span>
              );
            } else if (data === "notSent") {
              setTitle(
                <span style={{ color: "red" }}>
                  Error while sending emailForgotPassword create new account or
                  contact admin
                </span>
              );
            } else {
              setTitle(
                <span style={{ color: "green" }}>
                  Check your emailForgotPassword and Log in!
                </span>
              );

              setTimeout(() => {
                handleClose();
              }, 1500);
            }
          }, 2000);
        })
        .catch((err) => {
          setTitle(
            <span style={{ color: "red" }}>
              Error while sending emailForgotPassword create new account or
              contact admin
            </span>
          );
        });
    }
  };
  return (
    <Modal
      show={showForgotPassword}
      onHide={() => handleClose()}
      style={{ fontFamily: "Signika" }}
    >
      <Modal.Header closeButton style={{ textAlign: "center" }}>
        <Modal.Title>{title}</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <form>
          <div className="mb-3">
            {emailLabel}
            <input
              type="text"
              className="form-control"
              id="emailForgotPassword"
              value={emailForgotPassword}
              onChange={(e) => setEmailForgotPassword(e.target.value)}
            />
          </div>
        </form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={getCredentials}>
          <span className="text-primary">
            {localStorage.getItem("localLoading") === 6
              ? "Loading..."
              : "Get Password"}
          </span>
        </Button>

        <Button variant="secondary" onClick={handleClose}>
          <span className="text-primary">Close</span>
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ForgotPassword;
