import { useState } from "react";
import { Modal } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import React from "react";

const ForgotPassword = ({ showForgotPassword, setShowForgotPassword, setDisplay}) => {
  const [email, setEmail] = useState("");
  const [emailLabel, setEmailLabel] = useState(
    <label htmlFor="email" className="col-form-label">
      Enter email<span style={{ color: "red" }}>*</span>
    </label>
  );
  return (
    <Modal
      show={showForgotPassword}
      onHide={() => {setShowForgotPassword(false); setDisplay}}
      style={{ fontFamily: "Signika" }}
    >
      <Modal.Header closeButton style={{ textAlign: "center" }}>
        <Modal.Title>Retrieve Credentials</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <p className="lead">Enter email</p>
        <form>
          <div className="mb-3">
            {emailLabel}
            <input
              type="text"
              className="form-control"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
        </form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary">
          <span className="text-primary">Get Password</span>
        </Button>
        <Button
          variant="secondary"
          onClick={() => setShowForgotPassword(false)}
        >
          <span className="text-primary">Close</span>
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ForgotPassword;
