import { useState } from "react";
import { Modal } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import React from "react";

const ForgotPassword = ({
  showForgotPassword,
  setShowForgotPassword,
  setDisplay,
}) => {
  const [title, setTitle] = useState(<span>Forgot Password</span>);
  const [email, setEmail] = useState("");
  const [emailLabel, setEmailLabel] = useState(
    <label htmlFor="email" className="col-form-label">
      Enter email<span style={{ color: "red" }}>*</span>
    </label>
  );

  const handleClose = () => {
    setEmail("");
    setEmailLabel(
      <label htmlFor="email" className="col-form-label">
        Enter email<span style={{ color: "red" }}>*</span>
      </label>
    );
    setTitle(<span>Forgot Password</span>);
    setShowForgotPassword(false);
    setDisplay(true);
  };

  const getCredentials = () => {
    // email is empty
    if (email.length === 0) {
      setEmailLabel(
        <label htmlFor="email" className="col-form-label">
          <span style={{ color: "red" }}>
            Enter Email<span style={{ color: "red" }}>**</span>
          </span>
        </label>
      );
    } else {
      // API CALL

      //   Closing...
      handleClose();
    }
  };
  return (
    <Modal
      show={showForgotPassword}
      onHide={() => handleClose()}
      style={{ fontFamily: "Signika" }}
    >
      <Modal.Header closeButton style={{ textAlign: "center" }}>
        <Modal.Title>{title}</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <form>
          <div className="mb-3">
            {emailLabel}
            <input
              type="text"
              className="form-control"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
        </form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={() => getCredentials()}>
          {localStorage.getItem("Loading") ? (
            <span style={{ color: style.textColor }}>Loading...</span>
          ) : (
            <span style={{ color: style.textColor }}>Submit</span>
          )}
          <span className="text-primary">{localStorage}</span>
        </Button>
        <Button variant="secondary" onClick={() => handleClose()}>
          <span className="text-primary">Close</span>
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ForgotPassword;
