import { useState } from "react";
import { Modal } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import React from "react";

const ForgotPassword = ({
  showForgotPassword,
  setShowForgotPassword,
  setDisplay,
}) => {
  const [email, setEmail] = useState("");
  const [emailLabel, setEmailLabel] = useState(
    <label htmlFor="email" className="col-form-label">
      Enter email<span style={{ color: "red" }}>*</span>
    </label>
  );

  const handleClose = () => {
    setEmail("");
    setEmailLabel(
      <label htmlFor="email" className="col-form-label">
        Enter email<span style={{ color: "red" }}>*</span>
      </label>
    );
    setShowForgotPassword(false);
    setDisplay(true); 
  };

  const getCredentials = () => {
    // email is empty
    if (email.length === 0) {
      setEmailLabel(
        <label htmlFor="email" className="col-form-label">
          <span style={{ color: "red" }}>
            Enter ame <span style={{ color: "red" }}>**</span>
          </span>
        </label>
      );
    } else {
      // API CALL

      //   Closing...
      handleClose();
    }
  };
  return (
    <Modal
      show={showForgotPassword}
      onHide={() => handleClose()}
      style={{ fontFamily: "Signika" }}
    >
      <Modal.Header closeButton style={{ textAlign: "center" }}>
        <Modal.Title><span>Retrieve Credentials</span></Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <form>
          <div className="mb-3">
            {emailLabel}
            <input
              type="text"
              className="form-control"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
        </form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={()=>getCredentials()}>
          <span className="text-primary">Get Password</span>
        </Button>
        <Button
          variant="secondary"
          onClick={() => handleClose()}
        >
          <span className="text-primary">Close</span>
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ForgotPassword;
