import UserApiService from "../../../Service/UserApiService";
import { useState } from "react";
import { Modal } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import React from "react";

const ForgotPassword = ({
  showForgotPassword,
  setShowForgotPassword,
  setDisplay,
}) => {
  const [title, setTitle] = useState(<span>Forgot Password</span>);
  const [email, setEmail] = useState("");
  const [emailLabel, setEmailLabel] = useState(
    <label htmlFor="email" className="col-form-label">
      Enter email<span style={{ color: "red" }}>*</span>
    </label>
  );

  const handleClose = () => {
    setTitle(<span>Forgot Password</span>);
    setEmail("");
    setEmailLabel(
      <label htmlFor="email" className="col-form-label">
        Enter email<span style={{ color: "red" }}>*</span>
      </label>
    );
    localStorage.removeItem("Loading");
    setShowForgotPassword(false);
    setDisplay(true);
  };

  const getCredentials = () => {
    // email is empty
    if (email.length === 0) {
      setEmailLabel(
        <label htmlFor="email" className="col-form-label">
          <span style={{ color: "red" }}>
            Enter Email<span style={{ color: "red" }}>**</span>
          </span>
        </label>
      );
    } else {
      // API CALL

      UserApiService.forgotPassword(email)
        .then((response) => {
          localStorage.removeItem("Loading");
          const data = response.data;
          // email does not exist
          if (data === "notFound") {
            setTitle(
              <span style={{ color: "red" }}>
                Account does not exist with email provided
              </span>
            );
          } else if (data === "notSent") {
            setTitle(
              <span style={{ color: "red" }}>
                Error while sending email create new account or contact admin
              </span>
            );
          } else {
            setTitle(
              <span style={{ color: "green" }}>
                Check your email and Log in!
              </span>
            );

            setTimeout(() => {
              handleClose();
            }, 2000);
          }
        })
        .catch((err) => {
          setTitle(
            <span style={{ color: "red" }}>
              Error while sending email create new account or contact admin
            </span>
          );
        });
    }
  };
  return (
    <Modal
      show={showForgotPassword}
      onHide={() => handleClose()}
      style={{ fontFamily: "Signika" }}
    >
      <Modal.Header closeButton style={{ textAlign: "center" }}>
        <Modal.Title>{title}</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <form>
          <div className="mb-3">
            {emailLabel}
            <input
              type="text"
              className="form-control"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
        </form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={() => getCredentials()}>
          <span className="text-primary">
            {localStorage.getItem("Loading") ? "Loading..." : "Get Password"}
          </span>
        </Button>
        <Button variant="secondary" onClick={() => handleClose()}>
          <span className="text-primary">Close</span>
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ForgotPassword;
