class Loading {
    isLoading = () => {
      return localStorage.getItem("loading") !== null;
    };
    addLoading = () => {
      localStorage.setItem("loading", true);
    };
    removeLoading() {
      if (this.isLoading()) {
        localStorage.removeItem("loading");
      }
    }

  }

  export default new Loading();
